package com.example.ej2_sauco_pedropablo

import android.net.Uri
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import com.example.ej2_sauco_pedropablo.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {

    private lateinit var binding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(R.layout.activity_main)

    }

    private fun getMonumentos(): MutableList<Monumento> {
        val monumentos = mutableListOf<Monumento>()
        val monumento1 = Monumento(
            "https://viajes.nationalgeographic.com.es/medio/2020/11/06/shutterstock-1455114539_7970af5b_1200x630.jpg",
            "Catedral de Burgos",
            "Plaza de Santa María",
            Uri.parse("947 20 47 12"), "info@catedraldeburgos.es",
            "Con planta de cruz latina, consta de tres naves, transepto y 19 capillas distribuidas de manera irregular."
        )
        val monumento2 = Monumento(
            "data:image/jpeg;base64,",
            "Arco de Santa Maria",
            "Pl. Rey San Fernando, 9",
            Uri.parse("947 28 88 68"), "arcomariasanta@gmail.com",
            "El Arco de Santa María es uno de los monumentos más emblemáticos de la ciudad de Burgos"
        )
        val monumento3 = Monumento(
            "data:image/jpeg;base64,",
            "Monasterio de las huelgas",
            "Pl. Compás, s/n",
            Uri.parse("947 20 60 45"), "lashuelgas@gmail.com",
            "EL MONASTERIO DE LAS HUELGAS REALES fue fundado por los reyes don Alfonso y doña Leonor bajo la advocación y dedicada a Santa María, en 1180, y tuvo como finalidad la de servir de descanso o reposo (“huelga”) a los monarcas tanto en vida como tras la muerte"
        )
        val monumento4 = Monumento(
            "data:image/jpeg;base64,",
            "Cementerio de Sad Hill",
            "09613 Contreras",
            Uri.parse("665 35 53 22"), "cementeriosadhill@gmail.com",
            "El Cementerio de Sad Hill es una obra de arquitectura cinematográfica construida dentro de los límites municipales de Contreras y Santo Domingo de Silos"
        )
        monumentos.add(monumento1)
        monumentos.add(monumento2)
        monumentos.add(monumento3)
        monumentos.add(monumento4)

        return monumentos
    }
}