import com.example.ejerciciopruebas.databinding.ActivitySegundaActividadBinding

class SegundaActividad : AppCompatActivity() {
    var monumentos = mutableListOf<Monumento>()
    private lateinit var binding: ActivitySegundaActividadBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        //Creamos los productos
        binding = ActivitySegundaActividadBinding.inflate(layoutInflater)

        val producto1 = Monumento(
            1,
            "Bal√≥n",
            "data:image/png;base64,",
            "En mi cuarto bb",
            "947206045",
            "dkjbfgijf@gmail.com",
            "Si"
        )
        val producto2 = Monumento(
            2,
            "Telefono",
            "data:image/jpeg;base64,",
            "Donde tu gracia",
            "947206045",
            "dkjbfgijf@gmail.com",
            "Si"
        )
        val producto3 = Monumento(
            3,
            "Bugatti Chiron",
            "https://prod.pictures.autoscout24.net/listing-images/c413ab08-1a1e-4cec-a844-c6e1399f03a3_be080c9f-3d7c-4d82-ac8f-ae9a97522933.jpg/640x480.jpg",
            "A tomar por cleta",
             "947206045",
            "dkjbfgijf@gmail.com",
            "Si"
        )
        val producto4 = Monumento(
            4,
            "Chalet Salou",
            "https://res.listglobally.com/listings/5826196/73183173/7768867d4b8d4d0bcd626db77fb60964?mode=max&w=1920",
            "Av/Susmu",
            "947206045",
