            "Si"
        )
        monumentos.add(producto1)
        monumentos.add(producto2)
        monumentos.add(producto3)
        monumentos.add(producto4)
        super.onCreate(savedInstanceState)
        setContentView(binding.root)

       binding.buscar.setOnClickListener {
            val codigo = binding.txtProducto.text.toString()

            var buscar = false
            if (codigo.isEmpty()) {
                binding.txtProducto.setError("Falta C√≥digo")
            } else {
                val cod = Integer.parseInt(codigo)
                for (i in 0 until monumentos.size) {
                    if (cod == monumentos[i].codigo) {
                       mostrarDetalles(monumentos[i])
                        buscar = true
                    }
                }
                if (buscar == false) {
                    binding.txtProducto.setError("C√≥digo no existente")
                }
            }
        }
    }

    //SIRVE PARA SACAR LA FOTO Y LOS DATOS
    fun mostrarDetalles(monumento: Monumento) {
        intent = Intent(this, TerceraActividad::class.java)
        intent.putExtra("Monumento", monumento)
        startActivity(intent)
    }
}