package com.mjpg.userssp

import android.content.Intent
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.mjpg.userssp.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity(),EventosListener {

    private lateinit var binding: ActivityMainBinding
    private lateinit var usuarioAdaptador:UsuariosAdaptar
    private lateinit var linearLayoutManager: RecyclerView.LayoutManager
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)


        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)

        usuarioAdaptador = UsuariosAdaptar(getUsuario(),this)
        linearLayoutManager = LinearLayoutManager(this)
        binding.recyclerview.adapter = usuarioAdaptador
        binding.recyclerview.layoutManager = linearLayoutManager
    }

    private fun getUsuario(): MutableList<Usuario> {
        val usuarios = mutableListOf<Usuario>()
        val usuario1 = Usuario(1, "Maria", "Pardo", "https://www.quo.es/wp-content/uploads/2019/10/puede-un-tumor-cerebral-hacerte-ver-a-la-virgen-maria.jpg")
        val usuario2 = Usuario(2, "Luis Alberto", "Lazio", "data:image/jpeg;base64,")
        val usuario3 = Usuario(2, "Victor", "Ortega", "data:image/jpeg;base64,")
        val usuario4 = Usuario(2, "Ana", "García", "data:image/jpeg;base64,")
        usuarios.add(usuario1)
        usuarios.add(usuario2)
        usuarios.add(usuario3)
        usuarios.add(usuario4)
        return usuarios
    }

    override fun onClickListener(usuario: Usuario,posicion:Int) {
        intent= Intent(this, SegundaActividad::class.java)
        startActivity(intent)
        Toast.makeText(this,"El usuario es"+usuario.personalizado() + "posicion"+posicion.toString(), Toast.LENGTH_LONG).show()
    }
}
