package com.example.bd1saucopedropablo

import android.content.Context
import android.database.sqlite.SQLiteDatabase
import android.database.sqlite.SQLiteException
import android.database.sqlite.SQLiteOpenHelper
//Esta clase me permite crearme la estructura de mi base de datos
//context-->el boton(o la parte de xml donde lo vas a aplicar) donde vas a utilizarlo
//para utilizar para localizar rutas a la base de datos

//name--> nombre del archivo de base de datos-String: del archivo de base de datos, o nulo para una base de datos en memoria

//factory-->SQLiteDatabase.CursorFactory: para usar para crear objetos de cursor, o nulo para el valor predeterminado
//int: número de la base de datos (a partir de 1); si la base de datos es más antigua, se utilizará para actualizar la base de datos;

//version-->si la base de datos es más nueva, se utilizará para degradar la base de datos
class PortalBD(
    context: Context,
    name: String,
    factory: SQLiteDatabase.CursorFactory?,
    version: Int
) : SQLiteOpenHelper(context, name, factory, version) {

    //Creas las tablas (NO HABLAMOS DE DATOS, SOLO ESTRUCTURA)
    override fun onCreate(db: SQLiteDatabase?) {
        //El try te saca si has tenido un error en el SQL
        try {
            val crearTablaAlojamiento = "CREATE TABLE ${PortalDAO.TABLA_ALOJAMIENTOS} " +
                    "(codAlojamiento INTEGER, " +
                    " denominacion TEXT, localidad INTEGER," +
                    " precio INTEGER, imagen TEXT, telefono INTEGER, " +
                    " email TEXT, detalles TEXT, actividades TEXT," +
                    " favorito INTEGER )"
            //Con las !! decimos que sabemos que ese dato es no nulo, para poder ejecutarlo
            db!!.execSQL(crearTablaAlojamiento)
            val alojamiento1="INSERT into ${PortalDAO.TABLA_ALOJAMIENTOS} values (1, 'Hotel Puerta de burgos','Burgos',39," +
                    "'data:image/jpeg;base64,'," +
                    "947241000,'hotelpuertaburgos@gmail.com','Wifi-gratis, Servicio Habitaciones, Aire acondicionado'," +
                    "'Cabaña bioclimática rodeada de praderías y bosques bajo el macizo oriental de Picos de Europa. Este hotel sin pretensiones se encuentra a 2 km del Museo de la Evolución Humana y a 5 de la Catedral de Burgos',1);"

            val alojamiento2="INSERT into ${PortalDAO.TABLA_ALOJAMIENTOS} values (2, 'Hotel Rice Reyes Católicos','Burgos',34," +
                    "'data:image/jpeg;base64,'," +
                    "947222300,'hricereyesbrgs@gmail.com','Wifi-gratis, Desayuno, Aire acondicionado'" +
                    ",'Este hotel sin pretensiones se encuentra a 2 km del Museo de la Evolución Humana y a 5 de la Catedral de Burgos',0);"

            val alojamiento3="INSERT into ${PortalDAO.TABLA_ALOJAMIENTOS} values (3, 'Almirante Bonifaz','Pedro',38," +
                    "'data:image/jpeg;base64,'," +
                    "947791155,'bonifazalmirante@gmail.com','Servicio de lavandería, Wifi-gratis, Servicio de habitaciones'" +
                    ",'Este hotel refinado, situado a una manzana del río Arlanzón, se encuentra a 3 minutos a pie del Museo de la Evolución Humana y a 11 minutos a pie de la catedral de Burgos',0);"

            db!!.execSQL(alojamiento1)
            db!!.execSQL(alojamiento2)
            db!!.execSQL(alojamiento3)
        } catch (e: SQLiteException) {
        }
    }

    //Actualizas la estructura de la tabla -pudiendo añadir campos o modificarlos-(NO HABLAMOS DE DATOS, SOLO ESTRUCTURA)
    override fun onUpgrade(p0: SQLiteDatabase?, p1: Int, p2: Int) {

    }
}